INSERT INTO `foto` (`number`, `koncert_id`, `text`) VALUES
(1, 1, 'Placení diváci se rozehřívali kolou'),
(2, 1, 'Tak co hraje to?'),
(3, 1, 'Mistr basu měl soustředěný výraz&#8230;'),
(4, 1, '&#8230;a atmosféra v herně byla celkem vánoční'),
(5, 1, 'Sunshine of your love'),
(6, 1, 'Psychedelic band'),
(7, 1, 'Kytarová sóla byla jaksepatří dlouhá ale úderná&#8230;'),
(8, 1, '&#8230;El hrál jako ďábel&#8230;'),
(9, 1, '&#8230;a dokonce kapela dostala vánoční dar.'),
(10, 1, 'Nekousne mě to?'),
(11, 1, 'Je tam vůbec něco?'),
(12, 1, 'Spoluhudebníci dostali kazzoo, ale naší oblíbenou hitovku od Europe hrát nechtěli.'),
(13, 1, 'Juhy natruc zapoměl text a hledá ho na taháku&#8230;'),
(14, 1, '&#8230;aby mu v zápětí praskla struna, takže bylo ke slyšení a vidění sólo na basu a bicí s ladícím kytaristou v pozadí.'),
(15, 1, 'B3 rozhodně nepůsobí tak divoce jak hraje'),
(16, 1, 'EL je taťka od rodiny a nebude se na to nic měnit'),
(17, 1, 'Bluesman před zpocením'),
(18, 1, 'Někteří diváci se dosti odvázali&#8230;'),
(19, 1, '&#8230;a nekonečná sóla je příváděla na hranici extáze.'),
(20, 1, 'Mám kapse špačka tužky'),
(1, 2, 'Rockový koncert v 11. patře jsem začínali my&#8230;'),
(2, 2, '&#8230;již tradičně naší bluesovou peckou'),
(3, 2, '\"Nemůžu ti dát víc, než jsem dal\".'),
(4, 2, 'Světlené podmínky nebyly nejdříve pro fotografy ideální&#8230;'),
(5, 2, '&#8230;jenže pozitivní energie vycházející z kapely byla místa viditelná&#8230;'),
(6, 2, '&#8230;čímž se osvětlení značně zlepšilo, takže&#8230;'),
(7, 2, '&#8230;došlo i na humor&#8230;'),
(8, 2, '&#8230;proto Juhy sleduje po očku sleduje EL, jestli se směje.'),
(9, 2, 'Nervozita z hraní před kolegy nebyla malá, i ruce se trochu třásly'),
(10, 2, '&#8230;ale střízlivé publikum to neslo celkem statečně.'),
(11, 2, 'Fotografováni jsme byli zepředu i zezadu&#8230;'),
(12, 2, '&#8230;což nás nijak nedonutilo pózovat'),
(13, 2, 'Píseň střídala píseň až Rusty Rocket &#8230;'),
(14, 2, '&#8230;uvízli v zajetí rytmu'),
(15, 2, 'Zřejmě jsme byli také vtipní, jak lze vypozorovat z tváří návštěvníků'),
(16, 2, 'Následující kapela T.H.R. má opravdu démonickou image&#8230;'),
(17, 2, '&#8230;a ani kytarista nevystoupil během koncertu ze stínu'),
(1, 3, 'Večer byl ve znamení barevných světel, hlavně toho zeleného,&#8230;'),
(2, 3, '&#8230;záložního odposlechu,&#8230;'),
(3, 3, '&#8230;a víc než těsného kontaktu s diváky, kteří na pódium přímo přetékali&#8230;'),
(4, 3, '&#8230;jelikož nás při zvukové zkoušce nenapadlo že někdo přijde a na ty židle si sedne.'),
(5, 3, 'Juhy si pořídil stojan na texty&#8230;'),
(6, 3, '&#8230;což mu bylo prd platné, poněvadž osvětlení bylo sporadické (byť barevné).'),
(7, 3, 'Rozháněl trému vínem, takže se svojí oblíbené minerálky ani nedotkl&#8230;'),
(8, 3, '&#8230;což mělo za následek zvýšené zapomínání texů, překvapené pohledy spoluhudebníků&#8230;'),
(9, 3, '&#8230;a omluvné úsměvy.'),
(10, 3, 'Fotografové pořízovali odvážné fotografie na podiu&#8230;'),
(11, 3, '&#8230; a EL taktéž odvážně šťouchal laxní diváky pod žebra&#8230;'),
(12, 3, '&#8230;kteří při těch všech decibelech dokázali sedět zády.'),
(13, 3, 'Když došlo na oblíbenou hlášku \"Tohle je boogie woogie, to už nikdo neposlouchá\"'),
(14, 3, '&#8230;laxní diváci oblékli bundy a odešli do mrazu (díky čemuž mohla vzniknout tahle fotka).'),
(15, 3, 'Hráli jsme divoká sóla&#8230;'),
(16, 3, '&#8230;za to byli oslepováni blesky&#8230;'),
(17, 3, '&#8230;přesto jsme našli čas probrat co dělá rodina, děti, ženy, milenky a Rolling Stones.'),
(1, 4, 'New Club je příjemný sklepní klub o kterém se moc neví&#8230;'),
(2, 4, '&#8230;proto Juhy oblékl dres a spoléhal že se nebude mít kdo ptát co znamená číslo 98,&#8230;'),
(3, 4, '&#8230;EL se přihlásil zase svým tričkem k Pink Floyd před veřejností&#8230;'),
(4, 4, '&#8230;která nepřišla dvakrát v hojném počtu..'),
(5, 4, 'Protože jsme díky tomu hráli v rodinném kruhu&#8230;'),
(6, 4, '&#8230;koncert jsme si patričně užili.'),
(7, 4, '&#8230;každý po svém&#8230;'),
(8, 4, 'Už dlouho řešíme jaktože B3, takový divoký bubeník vypadá na fotkách tak klidně&#8230;'),
(9, 4, '&#8230;a EL, znamý bručoun a nespokojenec zase tak spokojeně.'),
(10, 4, 'V New Clubu se bubnovalo&#8230;'),
(11, 4, '&#8230;zpívalo&#8230;'),
(12, 4, '&#8230;vtipkovalo&#8230;'),
(13, 4, '&#8230;dokonce i na sólo na bubeny došlo.'),
(1, 5, 'V hospodě bylo poněkud těsno, &#8230;'),
(2, 5, '&#8230;zato koncert zvučil opravdický zvukař (který se rád vyfotil s naší kapelou),&#8230;'),
(3, 5, '&#8230;Juhy to tlačil ze všech sil&#8230;'),
(4, 5, '&#8230;a EL ho zamyšleně doprovázel.'),
(5, 5, 'V hospodě u Jima bylo docla těsno&#8230;'),
(6, 5, '&#8230;až někteří hudebníci křížili cestu navštěvníkům na toalet.'),
(7, 5, 'EL umí zpívat (když nad ním visí americký duch&#8230;)'),
(8, 5, '&#8230;což oceňuje jak Juhy&#8230;'),
(9, 5, '&#8230;tak samotné publikum.'),
(10, 5, 'Zároveň všichni hudebníci&#8230;'),
(11, 5, '&#8230;tedy s vyjímkou EL&#8230;'),
(12, 5, '&#8230;sledovali aktuální olympyjské zpravodajství.'),
(1, 7, 'Na našem prvním koncertě v Praze z nás nervozita jen čišela&#8230;'),
(2, 7, '&#8230;juhy se přetlačoval s congas bongas na pódiu&#8230;'),
(3, 7, '&#8230;EL už se radši do publika nedíval, protože bylo jaksi cizojazyčné a tudíž rozumělo prd&#8230;'),
(4, 7, '&#8230;Juhy se nemohl zahřát na provozní teplotu&#8230;'),
(5, 7, '&#8230;protože se pro samá conga nemohl hnout.'),
(6, 7, 'B3 hrál na bicí na kteých měl zakázáno hrát si se šroubky&#8230;'),
(7, 7, '&#8230;tak aspoň dostal pěknou fotku za odměnu.'),
(1, 8, 'Z majálesu byla zachráněna pouze jediná fotografie dokumentující jaká tam byla zima (a taky to že zvukař dal mikrofon ke špatnému zesilovači, který zrovna nebyl zapojený).'),
(1, 10, 'K hostinci u Horbenů přísluší střecha, která přišla vhod, nebo pršelo až hrůza&#8230;'),
(2, 10, '&#8230;před námi (mimo jiné) hráli sympatičtí a hovorní pukneři Why Not (Rusty Rocket v pozadí) z Rakovníka,&#8230;'),
(3, 10, '&#8230;a už nám ne tolik sympatičtí a nehovorní Turbo Revival (Rusty Rocket opět v pozadí) a další.'),
(4, 10, 'Zvuková zkouška Rusty Rocket byl rozjezd naplno&#8230;'),
(5, 10, '&#8230;a Juhy pověstný uhrančivým pohledem&#8230;'),
(6, 10, '&#8230;a pohybovými kreacemi&#8230;'),
(7, 10, '&#8230;cvičil svou už beztak spartakiádní sestavu.'),
(8, 10, 'El hrál ve svém příslovečném stoickém klidu&#8230;'),
(9, 10, '&#8230; na což se publikum chodílo dívat i zblízka.'),
(10, 10, 'Každý si koncert užíval po svém - Juhy se pokoušel vypotit nějaká kila&#8230;'),
(11, 10, '&#8230;B3 zase zapoměl vydatně povečeřet a přepadla ho slabost.'),
(12, 10, 'Kroužek mladých fotografů nebral ohled na řvoucí aparaturu v honbě za kvalitním snímkem&#8230;'),
(13, 10, '&#8230;lepší by bylo pořídit nahrávku, protože koncert to byl opravdu jedinečný.'),
(14, 10, 'Juhy se nechal zdokumentovat že už pije před a během koncertů jen vodu&#8230;'),
(15, 10, '&#8230;a B3 byl taktéž zdokumentován protože má hrozně málo fotek z koncertu.'),
(1, 13, 'Sólo v Nemá to cenu bylo tak procítěné&#8230;'),
(2, 13, '&#8230;že nikomu nedošlo že Juhy zavírá oči&#8230;'),
(3, 13, '&#8230;hlavně kvůli protislunci&#8230;'),
(4, 13, '&#8230; no a trochu taky kvůli příbuzenstvu v první řadě&#8230;'),
(5, 13, '&#8230;a možná kvůli představě již čekajících klobás.'),
(6, 13, 'Jak fotografie zaznamenala&#8230;'),
(7, 13, '&#8230;EL provozoval nějaké podivné tanoční kroky&#8230;'),
(8, 13, '&#8230;kroksunkroky&#8230;'),
(9, 13, '&#8230;a jiná prostná cvičení (co se to tam vlastně dělo?)'),
(10, 13, 'Juhy se vyhříval takřka na výsluní&#8230;'),
(11, 13, '&#8230;předváděl rockerské pózy&#8230;'),
(12, 13, '&#8230;no prostě to žral, holomek.'),
(13, 13, 'Pódium bylo na kapelu nezvykle velké&#8230;'),
(14, 13, '&#8230;a přesto že jsme se snažili natěsnat&#8230;'),
(15, 13, '&#8230;B3 seděl vzadu úplně sám&#8230;'),
(16, 13, 'Tak sbohem Marjánky - zase na podzim'),
(1, 15, 'Nejdřív jsme si hráli na zvukaře&#8230;'),
(2, 15, '&#8230;a nastavovali mikrofony \"off axis\"&#8230;'),
(3, 15, '&#8230;to vše za účel krátkého koncertu interní legendy&#8230;'),
(4, 15, '&#8230; T.H.R. věčně nastavující zrdcadlo.'),
(5, 15, 'Po půl hodině došlo na střídání&#8230;'),
(6, 15, '&#8230;Juhy předvedl svou chabou znalost italštiny&#8230;'),
(7, 15, '&#8230;a hned poté nový nástroj&#8230;'),
(8, 15, '&#8230;zakoupený v internetovém obchodě (k hrůze odborníků).'),
(9, 15, 'B3 měl lehké paličky a lehkou opici'),
(10, 15, '&#8230;zatímco řidič výpravy držel se zkrátka (chamtivě bráníc body).'),
(11, 15, 'Byť se to fotografům nepovedlo zachytit&#8230;'),
(12, 15, '&#8230;koncert probíhal lehce nestadardně&#8230;'),
(13, 15, 'neb i EL poskakoval (k radosti jiných).'),
(14, 15, 'Publikum bylo kultivované&#8230;'),
(15, 15, '&#8230;na rozdíl od kapely.'),
(16, 15, 'Pokochejme se novým přístkem do Rusty Rocket.'),
(17, 15, 'Závěrečný rodinný jam T.H.R. kontra Rusty Rocket&#8230;'),
(18, 15, '&#8230;nabíral na obrátkách&#8230;'),
(19, 15, '&#8230;že Juhy nebyl k zastavení&#8230;'),
(20, 15, '&#8230;a zlobil a zlobil'),
(1, 23, 'Pohled na stojan s notami je tu jen kvůli tomu, že dneska o klasiku rozhodně nepůjde&#8230;'),
(2, 23, '&#8230;dneska to totiž bude prostě maso.'),
(3, 23, 'Rusty Rocket totiž dneska křtí první cédéčko&#8230;'),
(4, 23, '&#8230;a proto se obložili hosty, například Markem Hořejšem/Dolejšem hrajícím zde na flétnu&#8230;'),
(5, 23, '&#8230;což získalo Juhyho respekt (neb sám zahraje na flétnu pouze \"Běží liška k táboru\")&#8230;'),
(6, 23, '&#8230;a taktéž ELův skoroúdiv.'),
(7, 23, 'Flétnu není lehké nazvučit a se pak Marek věnoval hlavně dojení harmoniky.'),
(8, 23, 'Hudebníci byli tentokrát klidní, vyrovnaní a nevystresovaní&#8230;'),
(9, 23, '&#8230;bodejť by ne, vžyť hráli na domácí půdě před domácím publikem.'),
(10, 23, 'Jelikož EL bránil dámský záchod, bylo třeba občas ohlíásit pauzu a pustit dámy vymočit&#8230;'),
(11, 23, '&#8230;čehož využili i pánové a proto na této fotografii polovina publika prostě není.'),
(12, 23, 'Na této fotografii není pro laika nic k vidění.'),
(13, 23, 'Druhá serie začla jamem, který způsobil Martinovo zasnění.'),
(14, 23, 'Po neprvním a neposledním jamu jsme pozvali našeho milého kmotra Yenkeeho mezi nás&#8230;'),
(15, 23, '&#8230;který vlastním pivem pokřtil naše album a popřál nám hodně úspěchů&#8230;'),
(16, 23, '&#8230;taktéž jsme publikum požádali, aby přispělo na charitativní akci \"Podpořte neznámou bluesovou legendu\".'),
(17, 23, 'Na následující skladbu, přišel mezi nás kytarista Lukáš Jandík&#8230;'),
(18, 23, '&#8230;a s jeho příchodem koncert začal dostávat rockové grády&#8230;'),
(19, 23, '&#8230;až B3 vyloudil, na své jinak kamenné tváři, úsměv.'),
(20, 23, 'Lukáše jsme občas odložili na střídačku naproti bubákovi&#8230;'),
(21, 23, '&#8230;a hráli powerplay.'),
(22, 23, 'EL hrál na láhev&#8230;'),
(23, 23, '&#8230;a Juhy byl spokojený a šťastný jak blecha'),
(24, 23, '&#8230;muziku si užíval jako obvykle, až na to&#8230;'),
(25, 23, '&#8230;že mu šotek přeházel hramoniky, ale snažil se to kamuflovat jako součást show&#8230;'),
(26, 23, '&#8230;což B3 z rytmu nemohlo vyhodit.'),
(27, 23, 'Lukáš si s raketama zahrál závěrečnou Hey Joe a po pár přídavcích&#8230;'),
(28, 23, '&#8230;bylo třeba začít balit&#8230;'),
(29, 23, '&#8230;zjistit kolik se prodalo cédeček&#8230;'),
(30, 23, '&#8230;a zapojit publikum do odnášení aparatury&#8230;'),
(31, 23, '&#8230;což se nestává často.'),
(32, 23, 'A pak jsme seděli a popíjeli a dělali poletový rozbor&#8230;'),
(33, 23, '&#8230;a poté jsme zamávali bubákovi na zdi a odplachtili každý&#8230;'),
(34, 23, '&#8230;do svého domečku.'),
(1, 24, 'Každý koncert začíná drátováním, tenhle však nezvykle v prostorách kina&#8230;'),
(2, 24, '&#8230;na pro nás až příliš velikém podiu.'),
(3, 24, 'Juhy tenhle koncert děsně žvanil&#8230;'),
(4, 24, '&#8230;rytmická dvojka ho však nanapomínala&#8230;'),
(5, 24, '&#8230;a to hlavně, protože se přiznal, že je mu právě dvacet děvět.'),
(6, 24, 'Rytmické duo udrželo pohromadě&#8230;'),
(7, 24, '&#8230;i proklínané, zběsilé kytarové pidlikání (pozorný čtenář si povšimne rozmazaných prstů.)'),
(8, 24, 'ELova příhoda s motocyklem Džáva nepadla úplně na úrodnou púdu, proto se raději věnoval muzice&#8230;'),
(9, 24, '&#8230;a žvanění ponechal jiném samozvanci/oslavenci&#8230;'),
(10, 24, '&#8230;který neustále něco vyprávěl&#8230;'),
(11, 24, '&#8230;že zapomínal hrát.'),
(12, 24, 'B3 je zatím bez úsměvu&#8230;'),
(13, 24, '&#8230;jelikož netušil, kolik se mu provede prodat cédéček početnému publiku.'),
(14, 24, 'Naše bluesovky dojímali nás i staré máničky v publiku&#8230;'),
(15, 24, '&#8230;a drsnější skladby naopak proplachovali máničkám ušní soupravičky.'),
(16, 24, 'Rusty Rocket dohráli koncert v bezva náladě&#8230;'),
(17, 24, '&#8230;a poděkovali publiku a odešli z pódia. A oslavenec se opil jak zákon káže.'),
(1, 29, 'Kontrolu zda máme všechno sbaleno tentokrát dělal Frank, &#8230;'),
(2, 29, '&#8230;na stěhování aparatury jsme měli hosta,&#8230;'),
(3, 29, 'a B3 - B3 měl jiné starosti než stěhování.'),
(4, 29, 'Po cestě jsme míjeli Královské kasíno v Dobřanech, místo prvního koncertu.'),
(5, 29, 'Stávění, zapojování, motnování&#8230;'),
(6, 29, '&#8230;i zvuková zkouška proběhly v nezvyklém poklidu&#8230;'),
(7, 29, '&#8230;i na ochutnávky chmeloviny zbyl čas.'),
(8, 29, 'Koncert jsme zahájili s Markem foukajícím do plechových krabiček&#8230;'),
(9, 29, '&#8230;a vlastně s námi koncert odehrál skoro celý.'),
(10, 29, 'Skalní fans trápili Juhyho lepší znalostí textů než má on sám&#8230;'),
(11, 29, '&#8230;Marek exceloval doprovodem na flétnu zobcovou&#8230;'),
(12, 29, '&#8230;a vyplňoval prázdnější místa skladeb.'),
(13, 29, 'Povzbuzeni početným publikem (18 platících), provozovali jsme show pořádnou, blusově smutnou&#8230;'),
(14, 29, '&#8230;i bluesově a veselou.'),
(15, 29, 'Marek dul do plechové krabičky (má ji mezi mikrofonem a sacím otvorem)&#8230;'),
(16, 29, '&#8230;B3 do toho řezal s citem (ne jako při poslední návštěvě Blues sklepa)&#8230;'),
(17, 29, '&#8230;a Juhy ždímal kytaru co to dalo.'),
(18, 29, 'Čtyřčlená sestava Rusty Rocket je&#8230;'),
(19, 29, '&#8230;jak vidno pro ELa důvodem ke spokojenosti&#8230;'),
(20, 29, '&#8230;a to i přesto, že jsme vezli zbytečně svůj zvuk (klub si mezitím pořídil aparaturu).'),
(21, 29, 'Mistr basu a mistr kovového hlasu&#8230;'),
(22, 29, '&#8230;snažili se udržet na uzdě, co samolibý sólový kytarista prováděl&#8230;'),
(23, 29, '&#8230; ačkoli to tak nevypadá, nebývá to vždy legrace&#8230;'),
(24, 29, 'Juhy tentokrát našel správnou harmoniku, takže Sbohem armádo už bylo bez ostudy.'),
(25, 29, 'Tato fotografie demonstruje rozfázovaně tovrbu bluesového tónu na kytaru (Příprava, Atak, Dozvuk, Vytažení) &#8230;'),
(26, 29, '&#8230;tato zase spícího bubeníka.'),
(27, 29, 'Přídavek byl publikem oceněn, neb přidávanou píseň znají a mají ji rádi&#8230;'),
(28, 29, '&#8230;jakou? Přijděte na koncert a uvidíte - &#8230;'),
(29, 29, '&#8230; - uvidíte partu rozjuchaných třicátníků.'),
(30, 29, 'Šipka na naší první promo fotografii jasně ukazuje kdo že je ten pravý bluesman.'),
(31, 29, 'Koncert se tedy povedl, balilo se v povznesené náladě&#8230;'),
(32, 29, '&#8230;HahaBibmbič nám ještě ukázal jak umí hrát stupnici na flétnu&#8230;'),
(33, 29, '&#8230;což B3 neumí a neskrývaně závidí.'),
(34, 29, 'Sakra proč měl Marek tohle napsáno na zádech?'),
(1, 34, 'Přestože Hifáč je nám známá plzeňská prostora,&#8230;'),
(2, 34, '&#8230;trpěli jsme lehkou trémou a taky lehkým smutkem neboť z pozvaných nedorazil nikdo,&#8230;'),
(3, 34, '&#8230;a předkoncertnímu shonu přihlíželi pouze fanoušci Drsné stěny.'),
(4, 34, 'Proto jsme poděkovali Drsné stěně za zapůjčení divácké základny&#8230;'),
(5, 34, '&#8230;a představili jí alespoň našeho klávesistu Laďu &#8230;'),
(6, 34, '&#8230;se kterým už je nás stejně jako brouků.'),
(7, 34, 'Pan zvukař bych uplně tichý a schovaný.'),
(8, 34, 'Na pravé straně pódia vznikla&#8230;'),
(9, 34, '&#8230;jakási rytmicko-harmonická sekce (která si navzájem nahazovala akordy)&#8230;'),
(10, 34, '&#8230; během ladění kytary s čerstvýmim, rozlaďujícími se, strunami.'),
(11, 34, 'B3 se bavil&#8230;'),
(12, 34, '&#8230;EL soustředil&#8230;'),
(13, 34, '&#8230;a B3 občas načuhoval i na cizí pracoviště&#8230;'),
(14, 34, '&#8230;na rozmazaného Juhyho.'),
(15, 34, 'Laďa měl problémy s větráky takže odbíhal pro oblečení&#8230;'),
(16, 34, '&#8230;zato B3 seděl v teple, což je důvodem blaženého úsměvu jak jeho &#8230;'),
(17, 34, '&#8230;tak všech ostatních hudebníků daleko od větráku.'),
(18, 34, 'Juhy hrál na foukačku a ač se tento nástroj v punku víceméně neužívá &#8230;'),
(19, 34, '&#8230;punkrockery to nijak neuráželo.'),
(20, 34, 'Poklidně a spokojeně bubnující B3&#8230;'),
(21, 34, '&#8230;náhle ztratil paličku čímž se postaral o menší show&#8230;'),
(22, 34, '&#8230;takže rytmus musela podržet jiná parta&#8230;'),
(23, 34, '&#8230;nedá se ovšem říci že by si toho publikum nějak všimlo.'),
(24, 34, 'Koncert jsme odehráli bez kolapsů&#8230;'),
(25, 34, '&#8230;a opět vypukl muraj&#8230;'),
(26, 34, '&#8230;a chaos přestavby a zvučení.'),
(27, 34, 'Po koncerní hodnocení bylo veselejší než předkoncertní přípravy &#8230;'),
(28, 34, '&#8230;takže jsme se vesele opili během koncertu Drsné stěny &#8230;'),
(29, 34, '&#8230; která se vůbec nešetřila&#8230;'),
(30, 34, 'Taktéž EL se nešetřil, neboť četl Muzikus v úplné tmě&#8230;'),
(31, 34, '&#8230;a na pokoncertním mejdanu se nešetřil už vůbec nikdo.'),
(1, 38, 'Z Plzně vyrážíme vozem osobním&#8230;'),
(2, 38, '&#8230;a ve Štychu přesedáme do ELovy dodávky&#8230;'),
(3, 38, '&#8230;abychom vyrazili do míst kde se točí pivo se jménem veskrze hudebním.'),
(4, 38, 'EL a Laďa se přetahovali při stěhovaní aparatury&#8230;'),
(5, 38, '&#8230;a dlouze diskutovali na témata kterým rozumí jen mistři černobílých klapek.'),
(6, 38, 'EL se rád fotografuje s hudebníky, tady se vyfotografoval s místním dudákem.'),
(7, 38, 'Rusty Rocket měli původně hrát před koncertem nedorazí-li ASPM do půl osmé&#8230;'),
(8, 38, '&#8230;ale ASPM dorazili přesně v 19:30.'),
(9, 38, 'EL po vzoru slavnější kapely vnutil pořadateli naše cédéčka k prodeji&#8230;'),
(10, 38, '&#8230;zatímco Juhy založil čtenářský kroužek.'),
(11, 38, 'ASPM začali hrát&#8230;'),
(12, 38, '&#8230;a ELovi z toho div nevypadli očiska&#8230;'),
(13, 38, '&#8230;zvláště z efektní hry na baskytaru Spáleného juniora.'),
(14, 38, 'Rusty Rocket se dostali na scénu před rozdováděné publikum&#8230;'),
(15, 38, '&#8230;takže si EL mohl konečně vyzkoušet postoj rockový rozkročný.'),
(16, 38, 'Aby koncert stál za to&#8230;'),
(17, 38, '&#8230;B3 v pozadí utahoval šrouby&#8230;'),
(18, 38, 'Laďa občas odbíhal od kláves&#8230;'),
(19, 38, '&#8230;protože se kapela chvíli slyšela slabě a chvíli silně&#8230;'),
(20, 38, '&#8230;a občas bylo třeba do mikrofonů dost řvát.'),
(21, 38, 'Juhy poskakoval z nožky na nožku, diváky nevyhnal však on, nýbrž poslední jedoucí spoj&#8230;'),
(22, 38, '&#8230;takže to rakety sbalily a o půlnoci odfičely do peří.'),
(1, 39, 'Po příjezdu byli panáčkové načapání jak se snaží odkrást k nebližšímu pohostinství&#8230;'),
(2, 39, '&#8230;ale byli včas odchyceni.'),
(3, 39, 'V hifáči jsme jako doma, hrajeme tam už podruhé, takže trému nemáme&#8230;'),
(4, 39, '&#8230;ani náš host není ztrémován&#8230;'),
(5, 39, '&#8230;jak vidno  z fotografie koncertní.'),
(6, 39, 'EL pozoruje zda to na něj spadne&#8230;'),
(7, 39, '&#8230;a k jeho pobavení to nespadlo.'),
(8, 39, 'B3 nic nechápe, protože nic neslyší&#8230;'),
(9, 39, '&#8230;když mu Juhy ukazuje, jak krásně si vyčistil chrup.'),
(10, 39, 'S Láďou, přestože není není suchar&#8230;'),
(11, 39, '&#8230;jen tak něco nehne - ani zubatá rybička, &#8230;'),
(12, 39, '&#8230;prostě se při popíjení kávěnky nedává rušit.'),
(13, 39, 'B3, starý mazák, směje se čerstvému ženáčovi&#8230;'),
(14, 39, '&#8230;který si stěžuje na to že mu překáží snubní prsten.'),
(15, 39, 'Marek dojil foukačku&#8230;'),
(16, 39, '&#8230; pomocí prostocviků, které ovládají jen skolení dechaří.'),
(17, 39, 'EL již podruhé odzpíval megahit Ranní&#8230;'),
(20, 39, '&#8230;a pak se koncert zvrhl do nezávazného jamu&#8230;'),
(18, 39, 'Část publika se okusovala&#8230;'),
(19, 39, '&#8230;a druhá část to první záviděla.'),
(21, 39, '\'Místo přídavku Juhy přednášel (po vzoru Jana Spáleného) báseň o slimákovi&#8230;'),
(22, 39, '&#8230;a pak už se jen balilo a stěhovalo&#8230;'),
(23, 39, '&#8230;dá se říci za tmy.'),
(1, 44, 'Kapela vyrážela pouze s půhodinovým zpožděním, které jsme s EM radostně pročekali u popelnic&#8230;'),
(2, 44, '&#8230;ale v Nezbavěticích nikoho zpoždění nevzrušovalo.'),
(3, 44, 'Rezatá Raketa 01 se až do stodoly nevyhrabala&#8230;'),
(4, 44, '&#8230;takže dále se nosilo&#8230;'),
(5, 44, '&#8230;a nosily celé rodiny.'),
(6, 44, 'Pokoušeli jsme se hrát jako o život&#8230;'),
(7, 44, '&#8230;ale publikum pořád odbíhalo&#8230;'),
(8, 44, '&#8230;na džontíka&#8230;'),
(9, 44, '&#8230;a pochopitelně pifko (jsme přece v Čechách).'),
(10, 44, 'Nahatý pejsek hledal v čem by se vyválel&#8230;'),
(11, 44, '&#8230;čtyři paka do toho mlátili hlava nehlava&#8230;'),
(12, 44, '&#8230;od pantáty vedli dráty&#8230;'),
(13, 44, '&#8230;někdo koukal na čudlíky&#8230;'),
(14, 44, '&#8230;a někdo zase tahal za páčku&#8230;'),
(15, 44, '&#8230;přesto koncert (alespoň dle hudebníků) odpovídal kvalitě pódia.'),
(1, 43, 'Hurá, z Rusty Rocket se stává kultura - hrají v kulturní kavárně&#8230;'),
(2, 43, '&#8230;při příjezdu kapely je kavárna prázdná&#8230;'),
(3, 43, '&#8230;tudíž kulturní umělci nespěchali s začátkem varieté.'),
(4, 43, 'Když začali, někteří z nich vystrkovali do publika pozadí&#8230;'),
(5, 43, '&#8230;protože bylo třeba profouknout ventily.'),
(6, 43, 'B3 načuhoval přes Juhyho, protože kapela&#8230;'),
(7, 43, '&#8230;hrála v netradičním složení a rozestavění&#8230;'),
(8, 43, '&#8230;se saxofonem místo kláves.'),
(9, 43, 'Sóla místo kytary obstarával saxofon&#8230;'),
(10, 43, '&#8230;a nakonec i nějaké to místo v hledišti se obsadilo.'),
(1, 36, 'Rusty Rocket se zeptali diváků zda chtěj koncert zahrát koncert tvrdě, a ELův výraz potvrzuje jak se kapela rozhodla&#8230;'),
(2, 36, '&#8230;to ale pro muzikanty v zadních řadách nemohlo být překvapením.'),
(3, 36, 'Jelikož právě začalo pršet, publikum se naskládalo pod střechu&#8230;'),
(4, 36, '&#8230;a kapela začala řádit (protože už konečně nehrála pro pět lidí, jako obvykle)&#8230;'),
(5, 36, '&#8230; i v zadních řadách na podiu bylo veselo.'),
(6, 36, 'Někteří tanečníci byli z řad hudebníků (na co asi hraje tento pán)&#8230;'),
(7, 36, '&#8230;zřejmě na to samé co tento.'),
(8, 36, 'V předních řadách dokonce tančila děvčata&#8230;'),
(9, 36, '&#8230;ale EL svoje potěšení nedal znát a tvářil se nadmíru přísně.'),
(10, 36, 'Juhy o děvčatech nic neví, nic totiž neviděl.'),
(11, 36, 'Nemalý zájem publika&#8230;'),
(12, 36, '&#8230;doháněl kapelu k nemalým výkonům&#8230;'),
(13, 36, '&#8230;které byly náležitě dokumentovány.'),
(14, 36, 'Rusty Rocket se zúčastnili i závěrečného finále festivalu&#8230;'),
(15, 36, '&#8230;kdy se na podiu sešli všichni účinkující (a někteří neúčinkující).'),
(1, 48, 'Buena Vista je pěkný a vybavený klub, takže Rezatá Raketa 01 byla vyložená co by dup, &#8230;'),
(2, 48, '&#8230;a protože jsme se měli dostavit se značným předstihem, zbyly dvě hodinky na kecání&#8230;'),
(3, 48, '&#8230;o letadlech a ovčelách.'),
(4, 48, 'Po deváté intimní amosféru klubu narušil náš modelářský kroužek&#8230;'),
(5, 48, '&#8230;netradičně doprovázený poltergeistem, kterém se podařilo zachztit na jediné fotografiii.'),
(6, 48, 'B3 převedl svůj buben, čerstvě označení imatrikulací označující příslušnost k Rezavým Raketám&#8230;'),
(7, 48, '&#8230;a také nám předvedl několik nezvyklých breaků (slíbil, že už to víckrát neudělá).'),
(8, 48, 'Laďovo sólo na cigárko bylo zařazeno z důvodu, krátké přestavby na pódiu&#8230;'),
(9, 48, '&#8230;jelikož jsme poprvé zařadili akustický set&#8230;'),
(10, 48, '&#8230;nezbytně doprovázený foukací harmonikou slepího harmonikáře.'),
(11, 48, 'Elektrická kytara se za akustickou měnila během skladby&#8230;'),
(12, 48, '&#8230;poté co Laďa zapreludoval jím nenáviděné klávesové sólo.'),
(13, 48, 'Juhy se pokoušil komunikovat s publikem a taktéž zkoušel na publikum řeč rukou&#8230;'),
(14, 48, '&#8230;když naznačoval, že by se to mohlo něco vrátit kousek zpátky (viz. Na plnej plyn).'),
(15, 48, 'Poté se kapela pokušela rozezpívat publikum v Kapesním blues&#8230;'),
(16, 48, '&#8230;a přestože nikdo nevěřil, že se to povede&#8230;'),
(17, 48, '&#8230;povedlo se a několik hlasů si přeci jen zapělo.'),
(18, 48, 'Představili jsme se kapelu,&#8230;'),
(19, 48, '&#8230;k ELově vynechali Ranní, a protože publikum v ovacích nijak nepřitlačilo&#8230;'),
(20, 48, '&#8230;počali jsme balit&#8230;'),
(21, 48, '&#8230;a dopíjet&#8230;'),
(22, 48, '&#8230;aby po nás zůstalo jen prázdné sklo (a po nás potopa).'),
(1, 47, 'Při zastávkách na benzínách se naplno projevila obesese B3, který neustále myl světla&#8230;'),
(2, 47, '&#8230;možná i proto jsme dorazilli na poslední chvíli do klubu vybavenými světly&#8230;'),
(3, 47, '&#8230;stylovou výzdobou&#8230;'),
(4, 47, '&#8230;řadou pomalovaných kytar&#8230;'),
(5, 47, '&#8230;a náhradními díly pro bubeníky.'),
(6, 47, 'Obhlédli jsme všechnu výzdobu a po půl hodině od příjezdu&#8230;'),
(7, 47, '&#8230;jsme počali hrát, nehledě na&#8230;'),
(8, 47, '&#8230;na značné stístění pracovního prostoru,&#8230;'),
(9, 47, '&#8230;což působilo malé problémy při přehazování kytar na akustický set&#8230;'),
(10, 47, '&#8230;a taktéž to způsobilo, že z Ladi byly vidět jen ruce a hlava&#8230;'),
(11, 47, '&#8230;a B3 byl na svém pracovišti zcela zazděn.'),
(12, 47, 'EL přezpíval své nachlazení&#8230;'),
(13, 47, '&#8230;a koncert probíhal normálně až do závěrečné Hey Joe&#8230;'),
(14, 47, 'kdy publikum začalo řádit tak, že jim i EL poděkoval&#8230;'),
(15, 47, '&#8230;ovšem ke vší smůle Juhymu praskla struna a její výměna byla zábavou pro celou kapelu&#8230;'),
(16, 47, '&#8230;stálo to však za to, jelikož došlo na set převzatých věci, na které se i tančilo, z protorových důvodů v řadě za sebou&#8230;'),
(17, 47, '&#8230;a i na tu kytaru za krkem došlo.'),
(1, 50, 'Po roce se u Havránků nic nezměnilo, až na to že se hudebníci nemohli ukrýt před případným deštěm&#8230;'),
(2, 50, '&#8230;ani nebyli chránění pódiem před rozdivočelými diváky.'),
(3, 50, 'THR začínali za světla a během jejich koncertu se rychle setmělo&#8230;'),
(4, 50, '&#8230;a Underfloor hráli potmě (protože EL zapoměl doma světla) &#8230;'),
(5, 50, '&#8230;početnému publiku to ovšem nijak nevadilo&#8230;'),
(6, 50, '&#8230;ani klávesistovi, na kterého nezbyla linka do koncového stupně (sorry).'),
(7, 50, 'Uderfloor mají dechaře (jednoho jsme si půjčili na jam)&#8230; '),
(8, 50, '&#8230;a hrají jak jinak (v plzni) než punk a ska. '),
(9, 50, 'Staří párdové začínali známým hitem starých páprdů&#8230;'),
(10, 50, '&#8230;ve kterém B3 předvede co v něm je&#8230; '),
(11, 50, '&#8230;taktéž Laďa potrápí svůj Korg.'),
(12, 50, 'Juhymu při úvodním odpočítávání přepadla mařena do obličeje&#8230;'),
(13, 50, '&#8230;což se ELovi stát nemůže (i když hraje In the Flesh) &#8230;'),
(14, 50, '&#8230;a Juhy se z vlasů se vyprostil až před další písní (povšimněte si narychlo zakoupeného reflektoru).'),
(15, 50, 'Kerio fan klub nám zůstal věrný&#8230;'),
(16, 50, '&#8230;i přesto že jsme (pro změnu) toho bigbítu moc nezahráli&#8230;'),
(17, 50, '&#8230;takže jsme nějaké to publikum měli.'),
(1, 59, 'Hudební odpoledne zahájila slečna, jenž se vetřela do přízně Lubošovi, který se stal ochotně zvukařem&#8230;'),
(2, 59, '&#8230;publikum to dokonce pozdvihlo se sedacích ploch&#8230;'),
(3, 59, '&#8230;aby při další kapele na ně zase usedlo (aneb Rezatý Rakety na plnej plyn).<br>'),
(4, 59, 'Přesto někteří otrlci se na nohou udrželi a to hlavně proto&#8230;'),
(5, 59, '&#8230;aby viděli místní legendu THR, ve které si mimo jiné taky zapěl Juhy.'),
(1, 61, 'Koncert zahájil domácí Stop-Time,&#8230;'),
(2, 61, '&#8230;který si od Rezatých Raket zapůjčil bubeníka Jirku&#8230;'),
(3, 61, '&#8230;který je kromě cyklistiky taktéž znám konzumací nealko piva.'),
(4, 61, 'A pak už zahájili Rezatý Rakety, Juhy chtěl udělat dojem hlasitostí aparátu&#8230;'),
(5, 61, '&#8230;a nakonec udělal dojem Luboš svou prapodivnou basou&#8230;'),
(6, 61, '&#8230;a Jirka malácením do blán.'),
(7, 61, '&#8230;Juhy se díval na kytaru jako by ji viděl poprvé&#8230;'),
(8, 61, '&#8230;a Luboš dělal že to nevidí'),
(9, 61, 'V Hifi klubu bylo narváno (a Jack měl narozeniny)&#8230;'),
(10, 61, '&#8230;že se z toho Jirka chytal za hlavu.'),
(11, 61, 'Ač se to dle fota nezdá, Juhy mluvil nezvykle málo&#8230;'),
(12, 61, '&#8230;koneckonců proč taky když má v kapele Luboše.'),
(19, 61, 'Rezatý Rakety nakonec provedli kompletní show&#8230;'),
(20, 61, '&#8230;i na malý jam došlo.'),
(21, 61, 'Honzu Máchu dostal náš bubeník natolik, že mu skládal poklony a pokusil se ho odloudit na  Jam session.'),
(1, 62, 'V Chebu nás přivítalo prostředí důvěrně známé&#8230;'),
(2, 62, '&#8230;avšak buhvíproč očekávali jsme pódium sálu ve Svobodě&#8230;'),
(3, 62, '&#8230;a místo toho nás očekával nadšený Venca se svým megalomanským aparátem.'),
(4, 62, 'První kapela se ochotně vyfotila s Juhym&#8230;'),
(5, 62, '&#8230;odehrála svůj divoký, půlhodinový set a&#8230;'),
(6, 62, '&#8230;konečně nastoupily Rezatý Rakety, které museli předvést kompletní pódiový cirkus&#8230;'),
(7, 62, '&#8230;zpívajícího Jíru&#8230;'),
(8, 62, '&#8230;všelijaké taneční kroky a poskoky&#8230;'),
(9, 62, '&#8230;a další takové ty věci co hudebníci běžně dělají.'),
(10, 62, 'Pak Jirka pochytal činely a uvolnili jsme prostor hvězdě večera&#8230;'),
(11, 62, '&#8230;místním Václavovým Bluesbreakers, během jejichž koncertu&#8230;'),
(12, 62, '&#8230;se fanynky fotily s hudebníky Rezatých Raket.'),
(1, 63, 'Nálada na lodi je víc než dobrá, takže festival plzeňských bluesmanů může začít&#8230;'),
(2, 63, '&#8230;první kapelou naší bluesové sešlosti jsou Rainy Days, pěkně nastoupení v řadě&#8230;'),
(3, 63, '&#8230;s hudebním prostituem Radkem Havlem na ságo&#8230;'),
(4, 63, '&#8230;s Tomášem Pechem, taktéž prostitujícím v  nemálo plzeňských kapelách.<br>'),
(5, 63, 'To dobou je publikum ještě ztuhlé, lež rozehřívá se.'),
(6, 63, 'Další kapelou v pořadí je Stop Time trio, posílené na kvartet věčným záskokařem Jirkou na bicí a opět s Radkem na ságo&#8230;'),
(7, 63, '&#8230;.a s Honzou Máchou v popředí i s novým nástrojem (ten den se vůbec nějak předváděly nové nástroje.'),
(8, 63, 'Pak nastroupila Blue Zebra 3 a&#8230;'),
(9, 63, '&#8230; a jejich koncert byl vrcholem večera.'),
(10, 63, 'Radek Chejlava se předváděl se svým novým telátkem <br> (s nímž má trvalý harmonický vztah založený na vzájemných sympatiích)&#8230;'),
(11, 63, '&#8230;a Eda propotil triko.'),
(12, 63, 'A pak už došlo i na Rezatý Rakety&#8230;'),
(13, 63, '&#8230;zpívajícího Jíru za bicíma&#8230;'),
(14, 63, '&#8230;obvyklé rokerské pózy&#8230;'),
(15, 63, '&#8230;to vše v teniskách od Kurta Cobaina&#8230;'),
(16, 63, '&#8230;Luboš ještě nadělil bluesmanům směsku hitů na foukačku, a už se jen můžeme těšit na další sraz plzeňských bluesmanů.'),
(1, 67, 'S úderem osmé nebylo v Buena Vistě úplně narváno&hellip;'),
(2, 67, '&hellip;nakonec Bluesbreakers hráli před poměrně zaplněným klubem.'),
(3, 67, 'Rezatý Rakety, po týdnech zkoušení vokálů a instrumentální ptákovin,&hellip;'),
(4, 67, '&hellip;podávaly soustředěný výkon,&hellip;'),
(5, 67, '&hellip;jak lze vyčíst z tváří&hellip;'),
(6, 67, '&hellip;všech hudebníků.'),
(7, 67, 'Jirka měl víc bubnů než kolik bylo třeba&hellip;'),
(8, 67, '&hellip;a Juhy pěl píseň o klukovi, co žiletku má na krku&hellip;'),
(9, 67, '&hellip;a co podrbe se v rozkroku.'),
(10, 67, 'A ti lidé se nakonec sešli&hellip;'),
(11, 67, '&hellip;a na poslední kapelu večera, Dlouhej kouř, už bylo plno.');
